import { Component, OnInit } from '@angular/core';
import { Drink } from '../../../models/drinks.model';

@Component({
  selector: 'app-drink-list',
  templateUrl: './drink-list.component.html',
  styleUrls: ['./drink-list.component.css']
})
export class DrinkListComponent implements OnInit {
  drinks: Drink[] = [
    {
      id: 1,
      name: 'Coffee',
      type: 'Coffee',
      description: 'Regular coffee',
      dateCreated: new Date('2023-06-24'),
      isActive: true
    },
    {
      id: 2,
      name: 'Latte',
      type: 'Coffee',
      description: 'Cafe latte',
      dateCreated: new Date('2023-06-24'),
      isActive: true
    }
  ];
  constructor() { }
  ngOnInit(): void {
    //Get list of drinks
    this.drinks.push()
  }
}
<p>drink-list works!</p>
<div class="container my-5">
  <h1 class="mb-3">Drinks</h1>

  <table class="table table-hover">
    <thead>
      <tr>
        <th>Id</th>
        <th>Name</th>
        <th>Type</th>
        <th>Description</th>
        <th>Available</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="Let drink of drinks">
        <td>{{ drink.id }}</td>
        <td>{{ drink.name }}</td>
        <td>{{ drink.type }}</td>
        <td>{{ drink.description }}</td>
        <td>{{ drink.isActive }}</td>
      </tr>
    </tbody>
  </table>
</div>
