import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { DrinkService } from '../../../services/drink.service';
import { Drink } from '../../../models/drink.model';
import { DatePipe } from '@angular/common';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-edit-drink',
  templateUrl: './edit-drink.component.html',
  styleUrls: ['./edit-drink.component.css'],
  providers: [DatePipe]
})
export class EditDrinkComponent implements OnInit {

  updateDrinkRequest: Drink = {
    id: 0,
    name: '',
    description: '',
    dateCreated: new Date(),
    formattedDateCreated: '',
    isActive: true,
    image: '',
    imageNew: ''
  };

  constructor(private route: ActivatedRoute, private drinkService: DrinkService, private router: Router, private datePipe: DatePipe, private http: HttpClient) { }

  ngOnInit(): void {
    // To grab id of the current drink record
    this.route.paramMap.subscribe({
      next: (params) => {
        const id = params.get('id');

        if (id) {
          //call api
          this.drinkService.getDrink(id)
            .subscribe({
              next: (response) => {
                this.updateDrinkRequest = response;
                this.updateDrinkRequest.formattedDateCreated = this.datePipe.transform(response.dateCreated, 'MMMM d, y h:mm a');
                this.updateDrinkRequest.image = 'data:image/png;base64,' + response.image;
              }
            });
        }

      }
    });
  }

  updateDrink() {
    this.drinkService.updateDrink(this.updateDrinkRequest.id.toString(), this.updateDrinkRequest)
      .subscribe({
        next: (response) => {
          this.router.navigate(['drinks']);
        }
      });
  }


  deleteDrink() {
    // Delete the drink, close the modal and redirect to the drink list page.
    this.drinkService.deleteDrink(this.updateDrinkRequest.id.toString())
      .subscribe({
        next: (response) => {
          document.getElementById("closeBtn")?.click();
          this.router.navigate(['drinks']);
        }
      });
  }

  confirmImageUpdate() {
    if (confirm("Are you sure to update the image?")) {
      /*
      if (this.updateDrinkRequest.imageNew) {
        this.drinkService.updateDrinkPhoto(this.updateDrinkRequest.id.toString(), this.updateDrinkRequest.imageNew)
        .subscribe({
          next: (response) => {
            this.router.navigate(['drinks']);
          }
        });
      } else {
        alert("Please select an image.")
      }
      */
    }
  }

  onChangeFile(event: any) {
    if (event.target.files.length > 0) {
      let file = event.target.files[0];
      let formData = new FormData();
      formData.append('file', file);

      this.http.put<Drink>('https://localhost:7281/api/drinks/photo/' + this.updateDrinkRequest.id.toString(), formData).subscribe((res: any) => {
        debugger
      });

      /*this.drinkService.updateDrinkPhoto(this.updateDrinkRequest.id.toString(), formData) //this.updateDrinkRequest.imageNew)
        .subscribe({
          next: (response) => {
            this.router.navigate(['drinks']);
          }
        });*/
    }
  }
}


<div class="py-4 bg-body-tertiary">
  <div class="container my-3">
    <h1 class="mb-3">Update Drink</h1>
    <form #form="ngForm" class="row" (ngSubmit)="updateDrink()">
      <div class="col-6">
        <div class="mb-3">
          <label for="id" class="form-label">ID</label>
          <input type="text" class="form-control" id="id" name="id" [(ngModel)]="updateDrinkRequest.id" disabled readonly>
        </div>
        <div class="mb-3">
          <label for="name" class="form-label">Name</label>
          <input type="text" class="form-control" id="name" name="name" [(ngModel)]="updateDrinkRequest.name">
        </div>
        <div class="mb-3">
          <label for="description" class="form-label">Description</label>
          <input type="text" class="form-control" id="description" name="description" [(ngModel)]="updateDrinkRequest.description">
        </div>
        <div class="mb-3">
          <label for="dateCreated" class="form-label">Date Created</label>
          <input type="text" class="form-control" id="dateCreated" name="dateCreated" date="dd-MM-yyyy" [(ngModel)]="updateDrinkRequest.formattedDateCreated" disabled readonly>
        </div>
        <div class="mb-3 form-check form-switch">
          <label for="isActive" class="form-label">Active</label>
          <input class="form-control form-check-input" type="checkbox" role="switch" id="isActive" name="isActive" [(ngModel)]="updateDrinkRequest.isActive">
        </div>
        <button type="submit" class="btn btn-primary">Save</button>
        <button type="button" id="deleteBtn" data-bs-toggle="modal" data-bs-target="#deleteConfirmModal" class="btn btn-danger mx-1">Delete</button>
        <button type="button" routerLink="/drinks" class="btn btn-secondary">Back</button>
      </div>
      <div class="col-6">
        <div class="mb-3">
          <img [src]="updateDrinkRequest.image" alt="Image" class="form-label row px-3"/>
          <label for="type" class="form-label">Image</label>
          <div class="input-group">
            <input type="file" accept="image/jpeg, image/png, image/gif" class="form-control" id="imageFile" aria-label="Upload" (change)="onChangeFile($event)">
            <button class="btn btn-outline-success" type="button" id="imageUpdateBtn" (click)="confirmImageUpdate()">Update</button>
          </div>
        </div>
      </div>
    </form>
  </div>
</div>


<!--Delete Confirmation Popup-->
<div class="modal" id="deleteConfirmModal" tabindex="-1">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title">Confirmation</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        <p>Are you sure you want to delete '{{updateDrinkRequest.name}}'?</p>
      </div>
      <div class="modal-footer">
        <button type="button" id="closeBtn" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        <button type="button" id="deleteConfirmed" class="btn btn-danger" (click)="deleteDrink()">Yes</button>
      </div>
    </div>
  </div>
</div>
