import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { DrinkService } from '../../../services/drink.service';
import { Drink } from '../../../models/drink.model';

@Component({
  selector: 'app-edit-drink',
  templateUrl: './edit-drink.component.html',
  styleUrls: ['./edit-drink.component.css']
})
export class EditDrinkComponent {

  editDrinkRequest: Drink = {
    id: 0,
    name: '',
    description: '',
    type: '',
    dateCreated: new Date(),
    isActive: true
  };

  constructor(private route: ActivatedRoute, private drinkService: DrinkService) { }

  ngOnInit(): void {
    this.route.paramMap.subscribe({
      next: (params) => {
        const id = params.get('id');

        if (id) {
          //call api
          this.drinkService.getDrink(id);
        }
      }
    });
  }
}
<div class="py-4 bg-body-tertiary">
  <div class="container my-3">
    <h1 class="mb-3">Add a New Drink</h1>
    <div class="row">
      <div class="col-6">
        <form #form="ngForm">
          <div class="mb-3">
            <label for="id" class="form-label">ID</label>
            <input type="text" class="form-control" id="id" name="id" [(ngModel)]="editDrinkRequest.id" readonly>
          </div>
          <div class="mb-3">
            <label for="name" class="form-label">Name</label>
            <input type="text" class="form-control" id="name" name="name" [(ngModel)]="editDrinkRequest.name">
          </div>
          <div class="mb-3">
            <label for="type" class="form-label">Type</label>
            <input type="text" class="form-control" id="type" name="type" [(ngModel)]="editDrinkRequest.type">
          </div>
          <div class="mb-3">
            <label for="description" class="form-label">Description</label>
            <input type="text" class="form-control" id="description" name="description" [(ngModel)]="editDrinkRequest.description">
          </div>
          <div class="mb-3">
            <label for="dateCreated" class="form-label">Date Created</label>
            <input type="text" class="form-control" id="dateCreated" name="dateCreated" [(ngModel)]="editDrinkRequest.dateCreated" readonly>
          </div>
          <div class="mb-3">
            <label for="isActive" class="form-label">Active</label>
            <input class="form-control form-check-input" type="checkbox" role="switch" id="isActive" name="isActive" [(ngModel)]="editDrinkRequest.isActive">
          </div>
          <button type="submit" class="btn btn-primary mx-1">Save</button>
          <button type="button" routerLink="../../drinks" class="btn btn-secondary mx-1">Back</button>
        </form>
      </div>
    </div>
  </div>
</div>
