import { Component, OnInit } from '@angular/core';
import { Drink } from '../../../models/drink.model';
import { DrinkService } from '../../../services/drink.service';

@Component({
  selector: 'app-drink-list',
  templateUrl: './drink-list.component.html',
  styleUrls: ['./drink-list.component.css']
})
export class DrinkListComponent implements OnInit {
  drinks: Drink[] = [];
  constructor(private drinkService: DrinkService) { }
  ngOnInit(): void {
    //Get list of drinks
    this.drinkService.getAllDrinks()
      .subscribe({
        next: (drinks) => {
          console.log(drinks);
        },
        error: (response) => {
          console.log(response);
        }
      });
  }
}
<div class="container my-5">
  <h1 class="mb-3">Drinks</h1>

  <table class="table table-hover" *ngIf="drinks && drinks.length > 0">
    <thead>
      <tr>
        <th>Id</th>
        <th>Name</th>
        <th>Type</th>
        <th>Description</th>
        <th>Available</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let drink of drinks">
        <td>{{ drink.id }}</td>
        <td>{{ drink.name }}</td>
        <td>{{ drink.type }}</td>
        <td>{{ drink.description }}</td>
        <td>{{ drink.isActive ? "Yes" : "No"}}</td>
      </tr>
    </tbody>
  </table>
  <p *ngIf="!drinks || drinks.length <= 0">No Drinks Found</p>
</div>
